    public int calculation;
    public double result;
            
    private void equalToActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_equalToActionPerformed
        // TODO add your handling code here:
        switch(calculation){
            case 1:
                result = Double.parseDouble(hidden.getText())+Double.parseDouble(shown.getText());
                shown.setText(Double.toString(result));
                hidden.setText("");
                break;
            
            case 2:
                result = Double.parseDouble(hidden.getText())-Double.parseDouble(shown.getText());
                shown.setText(Double.toString(result));
                hidden.setText("");
                break;
                
            case 3:
                result = Double.parseDouble(hidden.getText())*Double.parseDouble(shown.getText());
                shown.setText(Double.toString(result));
                hidden.setText("");
                break;
                
            case 4:
                result = Double.parseDouble(hidden.getText())/Double.parseDouble(shown.getText());
                shown.setText(Double.toString(result));
                hidden.setText("");
                break;
        
       
        } 
    }//GEN-LAST:event_equalToActionPerformed

    private void cActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cActionPerformed
        // TODO add your handling code here:
        int a = shown.getText().length();
        int b = shown.getText().length()-1;
        if(a>0){
            StringBuilder C = new StringBuilder(shown.getText());
            C.deleteCharAt(b);
            String cleared=C.toString();
            shown.setText(cleared);
        }    
        
        
    }//GEN-LAST:event_cActionPerformed
   
